// Scroll transition trigger
function initScrollingEffect() {
    var activeClass = 'fade-in';
    var windowElem  = jQuery( window );

    var willFadeInView = function( items ) {
        var windowAttr = {};
        windowAttr.height = windowElem.height(),
        windowAttr.top    = windowElem.scrollTop(),
        windowAttr.bottom = windowAttr.top + windowAttr.height;

        for( var x = 0; x < items.length; x++ ) {
            const item     = items[x];
            const elements = windowElem.width() > 767 ? item.desktop : ( item.mobile ? item.mobile : item.desktop );

            elements.elements.each( function() {
                var elem = jQuery( this );

                if( !elem.hasClass( activeClass ) ) {
                    var elemAttr = {};
                    elemAttr.height = elem.outerHeight(),
                    elemAttr.top    = elem.offset().top,
                    elemAttr.bottom = elemAttr.height + elemAttr.top;

                    /* Animation starts at the top */
                    if( elements.start === "top" ) {
                        if( elemAttr.top + ( windowElem.width() > 767 ? 150 : 10 ) <= windowAttr.bottom ) {
                            elem.addClass( activeClass );
                        }
                    /* Animation starts at the middle */
                    } else {
                        if( elemAttr.top + ( windowElem.width() > 767 ? (elemAttr.height / 2) - 150 : 10 ) <= windowAttr.bottom ) {
                            elem.addClass( activeClass );
                        }
                    }
                }
            } );
        }
    };

    /* default */
    var rows    = jQuery( ".with-fade-in .section" );

    willFadeInView( 
        [ 
            { 
                desktop: {
                    elements: rows
                }
            }
        ] 
    );
}

// Paragraph/Headings breaking down
function initBreakdownElements() {
    /* Get all elements with fade enabled */
    jQuery( ".with-fade-in [data-enable-fade=true]" ).each( function() {
        var offset = jQuery( this ).attr( "data-fade-offset" );
        var ctr = parseFloat( offset ? offset : 0 );

        /* Find text elements inside faded elements */
        jQuery( this ).find( `
            .styled-u, 
            .unstripped,
            .handwritten,
            .with-styled-u span,
            .with-dom span,
            p:not(.with-styled-u):not(.with-dom), 
            h1, 
            h2:not(.with-styled-u):not(.with-dom), 
            h3:not(.with-styled-u):not(.with-dom), 
            h4:not(.with-styled-u):not(.with-dom), 
            h5, 
            h6
        ` ).each( function() {
            var elem = jQuery( this );
            var text = elem.text();
            text = text.split( "-" ).join( " - " )
            text = text.split( "—" ).join( " — " );
            text = text.split( "/" ).join( " / " );
            text = text.split(/\s/);
            
            /* Breakdown first the text and convert it to span */
            elem.html( text.filter( s => s !== "" ).map( function( s ) { 
                return `<span class="fade">${ s }</span>`;
            }).join( ` ` ) );
            
            var spans = elem.find( "span.fade" );

            /* Check if span is in new line then implement additional delay */
            spans = jQuery.makeArray( spans.map( function( i, s ) {
                var elem = jQuery( s );

                /* Add delay if elem is in another breakline or elem is a child of header */
                if( 
                    elem.parents().is( ":header" ) || 
                    i === 0 || 
                    ( i > 0 && jQuery( spans[i - 1] ).offset().top !== elem.offset().top )     
                ) {
                    ctr = ctr + 0.08;
                }

                /* Remove whitespaces if element is a special character */
                if( 
                    elem.text() === "-" || 
                    elem.text() === "/" || 
                    elem.text() === "—" ||
                    jQuery( spans[i + 1] ).text() === "-" ||
                    jQuery( spans[i + 1] ).text() === "/" ||
                    jQuery( spans[i + 1] ).text() === "—"
                ) {
                    return `<span class="fade" style="--delay: ${ ctr }s">${ elem.text() }</span>`;
                }

                if(i + 1 === spans.length) {
                    return `<span class="fade" style="--delay: ${ ctr }s">${ elem.text() }</span>`;
                }

                return `<span class="fade" style="--delay: ${ ctr }s">${ elem.text() }</span> `;
            }) );

            if( elem.hasClass( "styled-u" ) ) {
                elem.attr( "style", "--delay: " + ( ctr + 0.4 ) + "s" );
            }

            /* Apply updated elements */
            elem.html( spans.join("") );
        });
    });

    // after breakdown, restructure underline
    initUnderlineStructure();
}

