// Number counter 
function initNumberCounter() {
    var counters = jQuery( ".number-counter .counter" );
    
    counters.each( function() {
        var counter = 0;
        var num = jQuery( this ).find( "div" );

        num.each( function() {
            var string = "";
            var value  = parseInt( jQuery( this ).attr( "data-num" ) );
            var max    = (10 * counter) + value;

            for( var x = 0; x <= max; x++ ) {
                var value = x > 9 ? `${x}`.charAt(`${x}`.length - 1) : x;
                string += `<label data-num="${ value }">${ value }</label>`;
            }

            jQuery( this ).html( `<div class="numbers">${ string }</div>` );
            

            counter = counter + 1;
        });
    });
}

// Structure for underline words
function initUnderlineStructure() {
    var underlined = jQuery( ".styled-u" );

    underlined.each( function() {
        var parent = jQuery( this );
        var recentSpan = null;
        var lineWidth = 0;
        var lineIndex = -1;
        var spans = jQuery( this ).find( "span:not(:empty)" );


        parent.find( ".line" ).remove();
        
        spans.each( function() {
            var width = jQuery( this ).width();
            var height = jQuery( this ).outerHeight();
            var top = ( jQuery( this ).offset().top - jQuery( this ).parents( ".with-styled-u" ).offset().top ) - ( jQuery( this ).parents( ".section" ).hasClass( "fade-in" ) ? 0 : 50 );
            var left = jQuery( this ).offset().left - jQuery( this ).parents( ".with-styled-u" ).offset().left;

            if( recentSpan === null || top !== recentSpan ) {
                lineIndex++;

                parent.append( `<div class="line" data-index="${ lineIndex }" style="left: ${ left }px; top: ${ top }px; height: ${ height }px;"></div>` );
                parent.find( ".line[data-index='" + lineIndex + "']" ).css({ width: `${ width }px` });

                lineWidth = 0;
                lineWidth = width;
                recentSpan = top;
            } else {
                lineWidth += width + (jQuery( window ).width() > 767 ? 10 : 5);

                parent.find( ".line[data-index='" + lineIndex + "']" ).css({ width: `${ lineWidth }px` });
            }
        });
    });
}
