// Initialize functionalities
function init() {
    var callout = jQuery( ".video-callout" );
        
    if( callout.length > 0 ) {
        var cookie = "jwa-video-callout";
        
        if( ! Cookies.get( cookie ) ) {
            jQuery( "body" ).addClass( "callout-enabled" );
        } else {
            setActiveClass();
            setTransitions();
        }

        callout.find( "button" ).on( 'click', function() {
            window.scrollTo(0, 0);
            
            Cookies.set( cookie, '1' );

            jQuery( this ).parents( ".video-callout" ).animate({ opacity: 0 }, 600, function() {
                jQuery( this ).addClass( "out" );
                jQuery( this ).find( "iframe" ).remove();
            });

            jQuery( 'body' ).removeClass( "callout-enabled" );

            setActiveClass();
            setTransitions();
        });
    } else {
        setActiveClass();
        setTransitions();
    }
}

