function initAnchorActive() {
    var currentpath = window.location.pathname.split( "/" );

    var childrenSlugs = {
        impact: ["africa", "arab-states", "arab-states", "asia-pacific", "europe-central-asia", "latin-america-the-caribbean"],
        thematic: ["thematic-focus"],
        stories: ["success-stories"]
    };

    var identifier = {
        main_header: "#main-header .header .menu > ul > li > a",
        mobile_header: "#main-header .mobile-nav .mobile-links > ul > li",
        mobile_impact_header: "#main-header .mobile-nav .mobile-links .submenu.impact ul > li",
        mobile_thematic_header: "#main-header .mobile-nav .mobile-links .submenu.thematic-focus ul > li",
        megamenu_thematic: "#main-header.custom-header .mega-wrapper .mega-nav-option.thematic-focus-nav .cell ul li a"
    };

    var isPageActive = url => {
        return currentpath.indexOf( url ) > -1;
    };

    var getChildren = key => {
        return key ? childrenSlugs[key] : null
    };

    jQuery( "#main-header .header .menu" ).addClass( "active" );

    // primary header
    jQuery( identifier.main_header ).each( function() {
        var children = getChildren( jQuery( this ).attr( "data-children" ) );
        var url = jQuery( this ).attr( "data-url" );

        if( children ) {
            if( children.filter( c => isPageActive( c ) ).length > 0 ) {
                jQuery( this ).addClass( "active" );
            }
        } else {
            if( isPageActive( url ) ) {
                jQuery( this ).addClass( "active" );
            }
        }
    });

    // megamenu thematic
    jQuery( identifier.megamenu_thematic ).each( function() {
        var url = jQuery( this ).attr( "data-url" );

        if( isPageActive( url ) ) {
            jQuery( this ).addClass( "active" );
        }
    });

    // mobile header
    jQuery( identifier.mobile_header ).each( function() {
        var children = getChildren( jQuery( this ).attr( "data-children" ) );
        var url = jQuery( this ).attr( "data-url" );

        if( children ) {
            if( children.filter( c => isPageActive( c ) ).length > 0 ) {
                jQuery( this ).addClass( "submenu-opened" );
                jQuery( this ).find( "> a" ).addClass( "active" );
            }
        } else {
            if( isPageActive( url ) ) {
                jQuery( this ).find( "> a" ).addClass( "active" );
            }
        }
    });

    // mobile impact 
    jQuery( identifier.mobile_impact_header ).each( function() {
        var parent = jQuery( this ).attr( "data-parent" );

        if( isPageActive( parent ) ) {
            jQuery( this ).addClass( "active" );

            jQuery( this ).find( "option" ).each( function() {
                if( isPageActive( jQuery( this ).attr( "data-url" ) ) ) {
                    jQuery( this ).attr( "selected", "selected" );

                    initCustomForms();
                }
            });
        }
    });

    jQuery( ".with-sidebar-layout .mobile-sidebar .multi-select > ul > li" ).each( function() {
        var slug = jQuery( this ).attr( "data-slug" );

        if( isPageActive( slug ) ) {
            jQuery( this ).addClass( "open" );
        }
    });

    jQuery( ".with-sidebar-layout .mobile-sidebar .multi-select > ul > li > ul > li > a" ).each( function() {
        var url = jQuery( this ).attr( "data-url" );

        if( isPageActive( url ) ) {
            jQuery( this ).addClass( "active" );
        }

        jQuery( this ).click( function() {
            jQuery( ".with-sidebar-layout .mobile-sidebar .multi-select" ).removeClass( "open" );
        });
    });

    // mobile thematic
    jQuery( identifier.mobile_thematic_header ).each( function() {
        var url = jQuery( this ).attr( "data-url" );

        if( isPageActive( url ) ) {
            jQuery( this ).addClass( "active" );
        }
    });
}

