function initSmoothScroll() {
    var smoothscroll = href => {
        jQuery( "body, html" ).animate({
            scrollTop: jQuery( href ).offset().top - jQuery( "#main-header .header" ).outerHeight() + 3
        } );
    }

    if( window.location.hash ) {
        smoothscroll( window.location.hash );
    }

    jQuery( "a" ).each( function() {
        var elem = jQuery( this );

        if( elem.attr( "href" ) && elem.attr( "href" ).indexOf( "#" ) > -1 ) {
            elem.click( function(e) {
                if( elem.attr( "href" ).charAt(0) === "#" ) {
                    e.preventDefault();
                }

                smoothscroll( elem.attr( "href" ).substring( elem.attr( "href" ).indexOf('#') ) );
            });
        }
    });
}

function initSwiperJS() {
    new Swiper(".callout-slider .swiper", {
        pagination: {
            el: ".swiper-pagination",
            clickable: true
        },
        navigation: {
            nextEl: ".button-next",
            prevEl: ".button-prev",
        },
        autoplay: {
            delay: 5000,
          }
    });

    jQuery(window).on('load resize orientationchange', function(){
        
        captionHeight = jQuery(".callout-slider .swiper .content-caption").outerHeight();

        if( jQuery(window).width() < 767 ) {

            captionHeight = captionHeight + 14;

        } else if( jQuery(window).width() >= 768 ) {
            
            captionHeight = captionHeight + 28;
            
        }
        
        jQuery(".callout-slider .swiper .swiper-navigation").css({
            'margin-bottom' : captionHeight,
        });
    });
}

function initHeader() {
    jQuery( "#main-header.custom-header .header .menu ul li a.has-submenu" ).each( function() {
        jQuery( this ).click( function(e) {
            e.preventDefault();
        });
    });
}

function initIntroBanner() {
    if( jQuery( window ).width() < 1025 ) {
        jQuery( ".image-reveal-card a" ).each( function() {
            jQuery( this ).click( function(e) {
                if( !jQuery( this ).hasClass( "clicked" ) ) {
                    e.preventDefault();

                    jQuery( ".image-reveal-card a" ).removeClass( "clicked" );
                    jQuery( this ).addClass( "clicked" );
                } else {
                    jQuery( this ).removeClass( "clicked" );
                }
            });
        });
    }
}

function initMatchheight(){

    var $option1 = {
        byRow: true,
        property: 'height',
        target: null,
        remove: false
    };

    var $option2 = {
        byRow: false,
        property: 'height',
        target: null,
        remove: false
    };

    jQuery('.callout-slider .swiper .swiper-slide .content-caption').matchHeight( $option2 );
}